INSERT INTO wp_posts VALUES ('34', '1', '2019-05-24 16:23:39', '2019-05-24 19:23:39', '<!-- wp:paragraph -->\n<p>\n\n1944 - Don Pascual Vigorita comienza a comercializar&nbsp;<strong>maderas</strong>.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1962 - El Ing. Biagio Vigorita&nbsp;incursiona&nbsp;junto a su padre en el negocio.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1982 - Se crea Vigorita&nbsp;<strong>Maderas</strong>&nbsp;S.R.L. cuyos socios son: &nbsp; &nbsp; &nbsp;<br> &nbsp;el Ing. Biagio Vigorita y su esposa.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1990 - Se incorporan a la sociedad sus hijos: &nbsp;Dr. Mariano y C.P.N. Silvia.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Se trata de una empresa familiar consolidada, pujante y en permanente cambio y actualización,&nbsp; tal como lo requieren los actuales mercados de globalización e internacionalización de las economías.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Se encuentra radicada en&nbsp;<a href=\"http://www.vigoritapremium.com.ar/images/mapa_vigorita.jpg\">Santo Tomé . Provincia de Santa Fe</a></p>\n<!-- /wp:paragraph -->', 'Historia', '', 'publish', 'closed', 'closed', '', 'historia', '', '', '2019-05-24 16:23:39', '2019-05-24 19:23:39', '', '0', 'http://localhost/vigorita/?page_id=34', '0', 'page', '', '0');
